class Timer{constructor(t,e){this.el=t,this.time=e,this.interval,this.start(),this.render()}start(){this.interval=setInterval(()=>this.tick(),1e3)}stop(){clearInterval(this.interval),localStorage.setItem("a",JSON.stringify(this.interval))}tick(){this.time--,this.render(),this.time<=0&&this.stop()}render(){let t=parseInt(this.time/3600),e=this.time%3600,i=parseInt(e/60),s=e%60;this.el.innerHTML=`<div>${t} <span>часов</span></div>\n\t\t                         <div>${i} <span>минут</span></div>\n\t\t\t\t\t\t\t               <div>${s} <span>секунд</span></div>`}}let el1=document.querySelector(".timer"),t1=new Timer(el1,5e4);document.querySelector(".action button").addEventListener("click",function(){t1.stop(),this.disabled=!0,this.innerHTML="Вот и сказочки конец!",this.classList.add("button-disabled")});